mapper.select:
SELECT line.id
FROM line
WHERE `departure` = #{departure} AND `destination` = #{destination}

SELECT T.*, S1.departureTime, S1.estimatedTime, S1.departure, S1.destination, M.name FROM ticket AS T LEFT JOIN ( SELECT S.id, S.departureTime, S.estimatedTime, L.departure, L.destination FROM service AS S LEFT JOIN line AS L ON S.lineId = L.id ) AS S1 ON T.serviceId = S1.id LEFT JOIN member AS M ON T.memberId = M.id WHERE
T.ticketCode = #{ticketCode}
T.id = #{id}

SELECT T.*, S1.departureTime, S1.estimatedTime, S1.departure, S1.destination, M.name FROM ticket AS T LEFT JOIN ( SELECT S.id, S.departureTime, S.estimatedTime, L.departure, L.destination FROM service AS S LEFT JOIN line AS L ON S.lineId = L.id ) AS S1 ON T.serviceId = S1.id LEFT JOIN member AS M ON T.memberId = M.id WHERE
T.email = #{memberEmail}
T.memberId = #{memberId}
ORDER BY departureDate, departureTime
